import React, { useState } from 'react';
import Header from './Header';

function App() {
  const [counter,setCounter] = useState(0); // useState retorna um array de duas posicoes. 1- valor; 2- funcao de atualizacao do valor

  function increment(){
    counter++;

    console.log(counter);
  }

  return (
    <div>
      <Header>Contador: {counter}</Header>
      <button onClick={increment}>Incrementar</button>
    </div>
  );
}

export default App;
