import React, { useEffect, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';

import 'bootstrap/dist/css/bootstrap.min.css';
import Accordion from 'react-bootstrap/Accordion';
import Button from 'react-bootstrap/Button';
import Card from 'react-bootstrap/Card';

import api from '../../services/api';
import './style.css'; 

export default function ProgCientifica(){

    const [ev, setEvents] = useState([]);
    const events = ev.map(event => (event.substring(13,17)));
    let eve12 = events.filter(event => event.DataPalestra == '2020-12-12T00:00:00');
    let eve13 = events.filter(event => event.DataPalestra == '2020-12-13T00:00:00');
    let eve14 = events.filter(event => event.DataPalestra == '2020-12-14T00:00:00');
    let eve15 = events.filter(event => event.DataPalestra == '2020-12-15T00:00:00');

    useEffect(() => {
        api.get('', { /*headers: { Authorization: eventId }*/ })
            .then(response => {
                console.log(response);
                setEvents(response.data);
            })
    }, ); 

    return(

        <div className="event-container">
            <div className="top-header">
                <h1>Programação <br/> Científica <span>.</span></h1>
            </div>           

            <Accordion defaultActiveKey="0">
                <Card>
                    <Card.Header>
                        <Accordion.Toggle as={Button} variant="link" eventKey="0">
                            12/12
                        </Accordion.Toggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey="0">
                        <Card.Body>
                        <ul>
                            {
                                events.filter(event => event.DataPalestra == '2020-12-12T00:00:00')
                                .sort((a) => a.HoraInicioPalestra)
                                .map(event => (
                                    <li key={event.PalestraId}>
                                        <strong>Ordem:</strong>
                                        <p>{event.OrdemPalestra}</p>

                                        <strong>Data:</strong>
                                        <p>{event.DataPalestra} | {event.HoraInicioPalestra} - {event.HoraFimPalestra} </p>

                                        <strong>Palestrante:</strong>
                                        <p>{event.PalestranteNome}</p>

                                        <strong>Título:</strong>
                                        <p>{event.TemaPalestra}</p>

                                        <strong>Descrição:</strong>
                                        <p>{event.DescricaoAtividade}</p>

                                    </li>
                                ))
                            }
                        </ul>
                        </Card.Body>
                    </Accordion.Collapse>
                </Card>
                <Card>
                    <Card.Header>
                        <Accordion.Toggle as={Button} variant="link" eventKey="1">
                            13/12
                        </Accordion.Toggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey="1">
                        <Card.Body>Hello! I'm another body</Card.Body>
                    </Accordion.Collapse>
                </Card>
                <Card>
                    <Card.Header>
                        <Accordion.Toggle as={Button} variant="link" eventKey="2">
                            14/12
                        </Accordion.Toggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey="2">
                        <Card.Body>Hello! I'm another body</Card.Body>
                    </Accordion.Collapse>
                </Card>
                <Card>
                    <Card.Header>
                        <Accordion.Toggle as={Button} variant="link" eventKey="3">
                            15/12
                        </Accordion.Toggle>
                    </Card.Header>
                    <Accordion.Collapse eventKey="3">
                        <Card.Body>Hello! I'm another body</Card.Body>
                    </Accordion.Collapse>
                </Card>
            </Accordion>

        </div>

    ) 
}
