    const [events, setEvents] = useState([]);

    useEffect(() => {
        api.get('', { /*headers: { Authorization: eventId }*/ })
            .then(response => {
                console.log(response);
                setEvents(response.data);
            })
    }, ); 

    return(

        <div className="event-container">
